background.func:
__ht__function(data, view) {
var type = data.a('state');
var state = "rgb(84,255,198,0.4)";
if(type == '4'){
    state = "rgb(255,0,77,0.4)";
}
if(type == '3'){
    state = "rgb(107,218,255,0.4)";
}
if(type == '2'){
    state = "rgb(247,247,22,0.4)";
}
if(type == '1'){
    state = "rgb(84,255,198,0.4)";
}
return state;
}

