renderHTML:
__ht__function(data, gv, cache) {const width = data.a('width');
const height = data.a('height');
const src = data.a('src');
const status = data.a('status');

if (!cache.htmlView) {
    cache.htmlView = document.createElement('video');
    cache.htmlView.style.width = '200%';
    console.log(width);
    cache.htmlView.style.height = '100%';
    cache.htmlView.setAttribute('id',"my-video");
    cache.htmlView.setAttribute('class','video-js');
    cache.htmlView.controls = true;
    cache.htmlView.setAttribute('preload',"auto");
    cache.childView = document.createElement('source');
    cache.childView.setAttribute('src',src);
    cache.childView.setAttribute('type','video/mp4');
    cache.htmlView.appendChild(cache.childView);
    if (status == '1') {
        cache.htmlView.stop();
    }
    else if (status == '2') {
        cache.htmlView.play();
    }

    cache.htmlView.layoutHTML = function() {
        gv.layoutHTML(data, cache.htmlView, false);
        //cache.htmlChart.resize();
    };
}else {
    if (status == '1') {
        cache.htmlView.stop();
        cache.childView.setAttribute('src','');
    }
    else if (status == '2') {
        cache.htmlView.play();
        cache.childView.setAttribute('src','');
    }
    cache.htmlView.layoutHTML = function() {
        gv.layoutHTML(data, cache.htmlView, false);
    };
}
return cache.htmlView;}
